mapper.select:
SELECT a.*, u.username as userName, mc.brand as motorcycleBrand, mc.model as motorcycleModel, mc.license_plate as licensePlate, (SELECT GROUP_CONCAT(si.item_name SEPARATOR ', ') FROM appointment_service_item asi LEFT JOIN service_item si ON asi.service_item_id = si.id WHERE asi.appointment_id = a.id) as serviceItemName FROM appointment a LEFT JOIN sys_user u ON a.user_id = u.id LEFT JOIN motorcycle mc ON a.motorcycle_id = mc.id WHERE a.merchant_id = #{merchantId}
AND a.status = #{status}
ORDER BY a.create_time DESC
